import { HttpHeaders } from '@angular/common/http';
import * as i0 from "@angular/core";
import * as i1 from "@angular/router";
import * as i2 from "@angular/common/http";
import * as i3 from "../store";
export class AuthGuard {
    constructor(router, http, storeModel) {
        this.router = router;
        this.http = http;
        this.storeModel = storeModel;
        this.baseUrl = "";
        this.baseUrl = storeModel.getBaseUrl();
    }
    async canActivate(route, state) {
        const token = localStorage.getItem("jwt");
        if (token) {
            return true;
        }
        const isRefreshSuccess = await this.tryRefreshingTokens(token);
        if (!isRefreshSuccess) {
            this.router.navigate(["login"]);
        }
        return isRefreshSuccess;
    }
    async tryRefreshingTokens(token) {
        const refreshToken = localStorage.getItem("refreshToken");
        if (!token || !refreshToken) {
            return false;
        }
        const credentials = JSON.stringify({ accessToken: token, refreshToken: refreshToken });
        let isRefreshSuccess;
        const refreshRes = await new Promise((resolve, reject) => {
            this.http.post(this.baseUrl + `/token/refresh`, credentials, {
                headers: new HttpHeaders({
                    "Content-Type": "application/json"
                })
            }).subscribe({
                next: (res) => resolve(res),
                error: (_) => { reject; isRefreshSuccess = false; }
            });
        });
        localStorage.setItem("jwt", refreshRes.token);
        localStorage.setItem("refreshToken", refreshRes.refreshToken);
        localStorage.setItem("user", JSON.stringify(refreshRes.user));
        isRefreshSuccess = true;
        return isRefreshSuccess;
    }
}
